'use strict';

// native
var fs = require('fs');
var util = require('util');

var FILENAME = './V2.json';
var SWAGGER;

function readSwagger(cb) {
    fs.access(FILENAME, function (err) {
        if (err) {
            cb(err);
            return;
        }
        SWAGGER = require(FILENAME);
        cb();
    });
}

function generateCode() {
    var i;
    var element;

    for (i in SWAGGER) {
        if (SWAGGER.hasOwnProperty(i)) {
            element = SWAGGER[i];
            console.log(i);
        }
    }
}

(function () {
    readSwagger(function (err) {
        if (err) {
            console.error(err.message);
            return;
        }
        generateCode();
    });
}());
